import React, {Component} from 'react';
import './App.css';
import Header from "./components/Header";
import CharCard from "./components/Character/";
import characters from "./characters.json";
import Wrapper from "./components/Wrapper";

class App extends Component {
  state = {
   characters,
   score:0,
   topScore: 0,
   message: "Click all the images just ONCE!"
  };

  componentDidMount(){
    this.setState({ characters: this.shuffleChar(this.state.character)})
  };

  shuffleChar = characters => {
    let newChar = characters.sort(function(a, b){return 0.5 - Math.random()})
    return newChar;
  };

  resetCards = characters => {
    const resetChar = characters.map(item => ({...item, clicked: false}))
    return this.shuffleChar(resetChar);
  };

  correctClick = newChar => {
    let newScore = this.state.score;
    newScore++
    let newTopScore = Math.max(newScore, this.state.topScore);

    this.setState({
      characters: this.shuffleChar(newChar),
      score: newScore,
      topScore: newTopScore,
    })
  };

  wrongClick = newChar => {
    this.setState({
      character: this.resetCards(newChar),
      score:0
    })
  };

  cardClick = id => {
    let clickCorrectly = false;
    const newChar = this.state.character.map(item =>{
      if(item.id === id){
        if(!item.clicked){
          item.clicked = true;
          clickCorrectly = true;
        }
      }
      return item;
    });
    clickCorrectly ? this.correctClick(newChar) : this.wrongClick(newChar);
  };

  render(){
    return (
    <div>
      <Header />
      <Wrapper>
      <CharCard/>
      </Wrapper>
    </div>
    );
}}

export default App;
